mapper.insert:
INSERT INTO replys(
      reply_no 
    , review_no
    , mem_id 
    , reply_content 
    , reply_date 
    , del_yn 
) VALUES (
      #{replyNo}
    , #{reviewNo}
    , #{memId}
    , #{replyContent}
    , SYSDATE
    , 'N'
)

mapper.update:
UPDATE replys
SET del_yn = 'Y'
WHERE reply_no = #{replyNo}
